show-largest-pack-objects-and-trim-your-waist-line/","# @author Antony Stubbs","# modified by @miguelgfierro","","# set the internal field spereator to line break, so that we can iterate easily over the verify-pack output","IFS=$'\\n';","","# list all objects including their size, sort by size, take top 20","objects=`git verify-pack -v .git/objects/pack/pack-*.idx | grep -v chain | sort -k3nr | head -n 20`","","echo \"All sizes are in MB. The pack column is the size of the object, compressed, inside the pack file.\"","","output=\"size,pack,SHA,location\"","for y in $objects","do","\t# extract the size in bytes","\tsize=$((`echo $y | cut -f 5 -d ' '`/1024/1024))","\t# extract the compressed size in bytes","\tcompressedSize=$((`echo $y | cut -f 6 -d ' '`/1024/1024))","\t# extract the SHA","\tsha=`echo $y | cut -f 1 -d ' '`","\t# find the objects location in the repository tree","\tother=`git rev-list --all --objects | grep $sha`","\t#lineBreak=`echo -e \"\\n\"`","\toutput=\"${output}\\n${size},${compressedSize},${other}\"","done","","echo $output | column -t -s ', '"],"stylingDirectives":[[{"start":0,"end":11,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c"}],[{"start":0,"end":8,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[],[{"start":0,"end":57,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":18,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":1,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":114,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":23,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":28,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[],[{"start":0,"end":107,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":4,"end":9,"cssClass":"pl-s"},{"start":4,"end":6,"cssClass":"pl-pds"},{"start":6,"end":8,"cssClass":"pl-cce"},{"start":8,"end":9,"cssClass":"pl-pds"},{"start":9,"end":10,